INSERT INTO `mw_article` VALUES ('85', '广告策划&nbsp;&nbsp;&nbsp;&nbsp;10名', null, '', '', '<p class=\"p0\" style=\"text-align:left;\"><span style=\"font-size:12.0000pt;font-family:\'宋体\';\"><strong>岗位职责：</strong></span></p>\n<p class=\"p0\" style=\"text-align:left;\"><span style=\"font-size:12.0000pt;font-family:\'宋体\';\"></span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\"><span style=\"color:#000000;\">-</span><span style=\"color:#000000;\"> 1</span></span><span style=\"font-size:12pt;font-family:\'宋体\';color:#000000;\">、</span><span style=\"font-size:12pt;font-family:\'宋体\';color:#000000;\">负责制订企业各类策划工作计划，并组织落实2.负责组织进行企</span><span style=\"font-size:12pt;font-family:\'宋体\';color:#000000;\">业整体形象策划、视觉识别系统策划设计，并及时提交策划方案</span><span style=\"font-size:12pt;font-family:\'宋体\';color:#000000;\">；</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\"></span></p>\n<p class=\"p0\" style=\"text-align:left;\"><span style=\"font-size:12.0000pt;font-family:\'宋体\';\"><span style=\"color:#000000;\">-</span><span style=\"color:#000000;\"> 2、</span></span><span style=\"font-size:12pt;font-family:\'宋体\';color:#000000;\">负责企业形象宣传策划，组织撰写宣传推广方案和广告创意设计</span><span style=\"font-size:12pt;font-family:\'宋体\';color:#000000;\">等</span><span style=\"font-size:12pt;font-family:\'宋体\';color:#000000;\">；</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\"></span></p>\n<p class=\"p0\" style=\"text-align:left;\"><span style=\"font-size:12.0000pt;font-family:\'宋体\';\"><span style=\"color:#000000;\">-</span><span style=\"color:#000000;\"> 3、</span></span><span style=\"font-size:12pt;font-family:\'宋体\';color:#000000;\">依据各项目的市场定位，参与编制项目总体营销计划和销售全案</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\"> </span><span style=\"font-size:12pt;font-family:\'宋体\';color:#000000;\">策划，并指导实施</span><span style=\"font-size:12pt;font-family:\'宋体\';color:#000000;\">；</span><span style=\"font-size:12.0000pt;font-family:\'宋体\';\"></span></p>\n<p class=\"p0\" style=\"text-align:left;