INSERT INTO `mw_question` VALUES ('73', '贝蒙天地锦轩户型点评', null, '', '', '<p><strong>客户要求：</strong></p>\n<p>客户姓名：赵老师&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</p>\n<p>楼盘地址：贝蒙天地锦轩</p>\n<p>设计风格：现代</p>\n<p>常住人口：2人</p>\n<p><strong>特点：</strong></p>\n<p>1、挑空六米的气派大客厅，如果装饰水晶吊灯、山水国画...尽可展现居家的非凡品味。</p>\n<p>2、餐厅与厨房动线相连，完整方便，可布置成阳台小花园让用餐别具浪漫情趣。</p>\n<p>3、南北通透，阳光清风自由流畅。</p>\n<p><strong>不足：</strong></p>\n<p>1、室内空间相对偏小。</p>\n<p>2、卫生间空气流通不足。</p>\n<p>3、占用面积较多，利用率不高。</p>', '2014-08-12 17:42:13', '2', null, '2014-08-12 17:42:13', null, '0', '0', '66', '1', '0');
