INSERT INTO `mw_single` VALUES ('5', '发展规划标题', null, '发展规划关键字', '发展规划摘要', '<p><strong><span style=\"font-size:16px;\">企业使命</span></strong> </p>\n<p>保障供水 服务社会 净化污水 保护环境</p>\n<p>&nbsp;</p>\n<p><br />\n</p>\n<p><strong><span style=\"font-size:16px;\">企业建设目标</span></strong> </p>\n<p>把绵阳市水务集团建设成为具有：一流的技术、一流的设备、一流的管理、一流的员工、一流的产品、一流的服务的现代化大型城市供排水企业。</p>\n<p><br />\n</p>\n<p><br />\n</p>\n<br />', '', null, '1', '1', null, '2015-02-13 10:17:12', '', '22');
