-- ----------------------------
-- Table structure for wc_member
-- ----------------------------
CREATE TABLE `wc_member` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '主键',
  `subscribe` enum('1','0') NOT NULL COMMENT '用户是否订阅该公众号标识，值为0时，代表此用户没有关注该公众号，拉取不到其余信息。 ',
  `openid` varchar(100) DEFAULT NULL COMMENT '用户的标识，对当前公众号唯一 ',
  `nickname` varchar(255) DEFAULT NULL COMMENT '用户的昵称',
  `remarkname` varchar(255) DEFAULT NULL COMMENT '备注名',
  `sex` enum('2','0','1') DEFAULT NULL COMMENT '用户的性别，值为1时是男性，值为2时是女性，值为0时是未知 ',
  `city` varchar(255) DEFAULT NULL COMMENT '用户所在城市',
  `country` varchar(255) DEFAULT NULL,
  `province` varchar(255) DEFAULT NULL COMMENT '用户所在省份',
  `language` varchar(255) DEFAULT NULL COMMENT '用户的语言，简体中文为zh_CN',
  `headimgurl` text COMMENT '用户头像，最后一个数值代表正方形头像大小（有0、46、64、96、132数值可选，0代表640*640正方形头像），用户没有头像时该项为空 ',
  `subscribetime` int(11) DEFAULT NULL COMMENT '用户关注时间，为时间戳。如果用户曾多次关注，则取最后关注时间 ',
  `unionid` int(11) DEFAULT NULL COMMENT '只有在用户将公众号绑定到微信开放平台帐号后，才会出现该字段',
  `unsubscribetime` int(11) DEFAULT NULL COMMENT '户用取消关注时间戳',
  `group` tinyint(4) unsigned DEFAULT '0' COMMENT '用户组别',
  PRIMARY KEY (`id`),
  UNIQUE KEY `openid` (`openid`)
) ENGINE=InnoDB AUTO_INCREMENT=137 DEFAULT CHARSET=utf8;

