CREATE TABLE `wc_message` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '主键',
  `openid` varchar(100) NOT NULL COMMENT '用户的OPENID',
  `msgtype` enum('5','4','3','2','1') NOT NULL COMMENT '消息类型 1:文本消息 2：图片消息 3：语音消息 4：视频消息  5：地理位置消息 6：链接消息\r\n',
